#include <iostream>
#include <math.h>
#include <vector>
#include <map>
using namespace std;
#define int long long
int n = 1000001;
int ans[1000001];
#define mxxxx 77777777
#define inf 7777
int dppp[inf];
vector<char> arr (n+1, true);
void save(){
    arr[0] = arr[1] = 0;
    ans[0] = ans[1] = 1;
    int k = 1;
    for (int i=2; i<=n; ++i){
        if (arr[i]){
          	k ++;
            if (i * 1ll * i <= n){
                for (int j=i*i; j<=n; j+=i){
                    arr[j] = 0;
                }
                
            }
        }
        ans[i] = k;
        //cout << i << " " << k << endl;
    }
}
signed main() {
    save();
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
    int t;
    cin >> t;
    
    while(t --){
        int u;
        cin >> u;
        cout << ans[u] << endl;
    }
    return 0;
}
