#include <bits/stdc++.h>
using namespace std;

void power(long long T[2][2], long long n)
{
	if (n == 0 || n == 1)
		return;

	power(T, n / 2);

	long long mod = 1000000009,x1,x2,x3,x4,x5,x6,x7,x8,x9;

	long long a1 = T[0][0], b1 = T[0][1];
	long long d1 = T[1][0], e1 = T[1][1];

	x1 = ((T[0][0] * a1) % mod + (T[0][1] * d1) % mod) % mod;
    x2 = ((T[0][0] * b1) % mod + (T[0][1] * e1) % mod) % mod;
    x3 = ((T[1][0] * a1) % mod + (T[1][1] * d1) % mod) % mod;
    x4 = ((T[1][0] * b1) % mod + (T[1][1] * e1) % mod) % mod;
    T[0][0] = x1;
    T[0][1] = x2;
    T[1][0] = x3;
    T[1][1] = x4;

	if (n % 2) {
        long long a = 9, b = 8;
        long long d = 1, e = 1;

        x1 = ((T[0][0] * a) % mod + (T[0][1] * d) % mod) % mod;
        x2 = ((T[0][0] * b) % mod + (T[0][1] * e) % mod) % mod;
        x3 = ((T[1][0] * a) % mod + (T[1][1] * d) % mod) % mod;
        x4 = ((T[1][0] * b) % mod + (T[1][1] * e) % mod) % mod;
        T[0][0] = x1;
        T[0][1] = x2;
        T[1][0] = x3;
        T[1][1] = x4;
	}
}
long long tribonacci(long long n)
{
	long long T[2][2] = {{ 9, 8 },
				{ 1, 1 }};

	if (n == 0 || n == 1)
		return 0;
	else
		power(T, n - 2);

	return (T[0][0] + T[1][0]) % 1000000009;
}

int main()
{
	long long t;
	scanf("%lld", &t);
	while (t--) {
        long long n;
        scanf("%lld", &n);
        if (n == 0)
            cout << "1\n";
        else
        if (n == 1)
            cout << "10\n";
        else
        printf("%lld\n", tribonacci(n + 2));
	}

