#include <bits/stdc++.h>
#define ll long long
ll b[100000];
using namespace std;
void cadane(ll array[],ll n){
  ll best=-1e18 , sum=-1e18;
  for (ll k=0;k<n;k++){
    sum = max(array[k],sum+array[k]);
    best = max(best,sum);
  }
  cout<<best<<endl;
}
int main(){
  ll x,y;
  cin>>x>>y;
  ll array[x];
  for(ll i=0;i<x;i++){
    cin>>array[i];
  }
  while(y--){
    ll ansa,ans;
    cin>>ansa>>ans;
    array[ansa-1]=ans;
    cadane(array,x);
  }
  return 0;
}
