#include <iostream>

using namespace std;

void solve(){
  int x, y;
  cin >> x >> y;
  if (x > 99){
    cout << 1;
  }
  else{
    int s = x - 2 * y, k = 1;
    while (s + x - y < 100){
      s = s + x - 3 * y;
      k += 1;
    }
    if (s > 99) cout << k;
    else cout << ++k;
  }
}

int main()
{
  int t = 1;
  while (t--) solve();
  return 0;
}
