#include <bits/stdc++.h>
#define ll long long

using namespace std;
#define int long long
bool isPrime(int n){
  if(n==2 || n==3) return true;
  if(n%2==0 || n%3==0) return false;
  for(int i=3; i*i<n; i++){
    if(n%i==0) return false;
  }
  return true;
}

signed main()
{
  ll n,k;
  cin >> n >> k;
  if(k==0){
    cout << "NO"; return 0;
  }
  ll i=0, j=n;
  ll qq = n+1;
  while(qq--){
    if(i*2+j*3 == k){
      cout << "YES";
      return 0;
    }
    i++;
    j--;
  }
  cout << "NO";
  return 0;
}
