#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n;
    cin >> n;
    if (n < 2) {
        cout << -1;
        return ;
    }
    list < int > lt;
    for (int i = 1; i <= n; i++) {
        lt.push_back(i);
    }

    while (lt.size() != 2)
    {
        int x = lt.front();
        lt.pop_front();
        lt.push_back(x);
        x = lt.front();
        lt.pop_front();
        lt.pop_front();
        lt.push_front(x);
    }
    int a = lt.front();
    lt.pop_front();
    int b = lt.front();
    if (a > b) swap(a, b);
    cout << a << " " << b << endl;
}

int main() {

    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);

    int t = 1, k = 3;
    // cin >> t;
    while (t--) solve();
    return 0;
}
