#include <bits/stdc++.h>
using namespace std;

// Function to find minimum XOR pair
int minXOR(int arr[], int n)
{
	// Sort given array
	sort(arr, arr + n);

	int minXor = INT_MAX;
	int val = 0;

	// calculate min xor of consecutive pairs
	for (int i = 0; i < n - 1; i++) {
		val = arr[i] ^ arr[i + 1];
		minXor = min(minXor, val);
	}

	return minXor;
}

// Driver code
int main()
{
    int test;
    cin >> test;
    while (test--) {
        int n;
        scanf("%d", &n);
        int arr[n];
        for (int i = 0; i < n; i++) {
            scanf("%d", &arr[i]);
        }
        cout << minXOR(arr, n) << endl;
    }
	return 0;
}
