#include <bits/stdc++.h>
#define int long long
using namespace std;

int minQueue(queue<int> &q)
{
    int min_elem = q.front();
    q.pop();
    q.push(min_elem);

    for (int i = 0; i < q.size(); i++) {
        int temp = q.front();
        q.pop();
        if (min_elem > temp) {
            min_elem = temp;
        }
        q.push(temp);
    }
    return min_elem;
}

int maxQueue(queue<int> &q)
{
    int max_elem = q.front();
    q.pop();
    q.push(max_elem);

    for (int i = 0; i < q.size(); i++) {
        int temp = q.front();
        q.pop();
        if (max_elem < temp) {
            max_elem = temp;
        }
        q.push(temp);
    }
    return max_elem;
}

int32_t main()
{

    int n = 5, m;

    queue<int>q;
    for (int i = 0; i < n; i++) {
        cin >> m;
        q.push(m);
    }

    int min_elem = minQueue(q);
    int max_elem = maxQueue(q);

    int sum = 0;

    while (!q.empty()) {
        sum = sum + q.front();
        q.pop();
    }

    cout << sum-max_elem << ' ';
    cout << sum-min_elem << ' ';

    return 0;
}
