def isprime(n):
	if n < 2:
		return 0
	i = 2
	while i * i <= n:
		if n % i == 0:
			return 0
		i += 1
	return 1
def solve(s):
	if s % 10 == 0:
		return "no"
	if isprime(s) and isprime(int(str(s)[::-1])):
		return "yes"
	return "no"
