#include<bits/stdc++.h>
using namespace std;

void f() {
 
    long long n;
    cin >> n;
    string s = to_string(n);
 
    auto sum = [&](string & x) {
        int h = 0;
        for (auto c : x) {
            h += (c - '0');
        }
        return h;
    };
 
    int nn = s.length();
    string a = s;
    int b = sum(s);
    for (int i = nn - 1; i > 0; i--) {
        string t = s;
        for (int j = i; j < nn; j++) {
            t[j] = '9';
        }
        for (int j = i - 1; j >= 0; j--) {
            if (t[j] != '0') {
                t[j] = (char)(t[j] - 1);
                break;
            }
        }
 
        int su = sum(t);
        if (su > b) {
            b = su;
            a = t;
        }
    }
 
    cout << stol(a);
 
 
 
}
 
int32_t main()
{
   f();
}
