import sys
sys.setrecursionlimit(10**9)
def mul(a, b, mod):
    mult = [[0 for row in range(3)] for col in range(3)]

    for i in range(3):
        for j in range(3):
            for k in range(3):
                mult[i][j] = (mult[i][j]+a[i][k]*b[k][j])%mod
    return mult

def power(a,n,mod):
    if n == 1: return a
    a = power(a,n//2,mod)
    a = mul(a,a,mod)
    if n%2 == 1: a = mul(a,[[1,2,3],[1,0,0],[0,1,0]],mod)
    return a

