#include<bits/stdc++.h>
using namespace std;

#define mx 164000
#define inf 15016
bool prime[mx];
long long m, dp[inf];

void saveprime() {
	prime[0] = prime[1] = 1;
	for (int i = 2; i * i < mx; i++) {
		if (!prime[i]) {
			for (int j = i*i; j < mx; j += i) {
				prime[j] = 1;
			}
		}
	}
	for (int i = 2; i < mx; i++) {
		if (!prime[i]) {
			dp[m++] = i;
		}
	}
}

int main() {
	int t, n;
	saveprime();
	scanf("%d", &t);
	while (t--) 
	{
		scanf("%d", &n);
		long long x = dp[n-1]*dp[n]; 
		long long y = dp[n-1]+dp[n]; 
		cout << x * y << endl;
	}
	return 0;
}
