#include<bits/stdc++.h>
using namespace std;

int main(){
    int s, t;
    cin >> s >> t;

    int a, b;
    cin >> a >> b;

    int n, m;
    cin >> n >> m;

    vector<int>aa(n);
    vector<int>bb(m);

    for (int i=0; i<n; i++){
        cin >> aa[i];
    }

    for (int i=0; i<m; i++){
        cin >> bb[i];
    }

    int ans_olma = 0;
    int ans_apln = 0;

    for (int i=0; i<n; i++){
        if (s <= a + aa[i] and a + aa[i] <= t){
            ans_olma += 1;
        }
    }

    for (int i=0; i<m; i++){
        if (s <= b + bb[i] and b + bb[i] <= t){
            ans_apln += 1;
        }
    }
    cout << ans_olma << "\n" << ans_apln;
    return 0;
}
