#include <bits/stdc++.h>
#define mod 1000000007
#define int long long
using namespace std;
 
int f(int b, int p){
    if(p == 0)
        return 1;
    int k = 1;
    while (p > 1) {
      if (p % 2) k = (k * b) % mod;
      b = (b * b) % mod;
      p /= 2;
    }
    return (b * k) % mod;
}
int32_t main(){
    int t;
    cin >> t;
    while(t--)
    {
        int a,b;
        cin >> a >> b ;
        cout << f(a,b) << endl;
    }
}
