#include <bits/stdc++.h>
using namespace std;

void solve(int n)
{
	if (n == 1)
    {
        cout << 1;
    }
    else
    if (n % 2 == 0)
    {
        int a = n / 2;
        int b = n / 2 + 1;
        n = n / 2;
        while (n--) {
            cout << a-- << " " << b++ << " ";
        }
    }
    else {
        int a = n / 2 + 1;
        int b = n / 2;
        n /= 2;
        while (n--) {
            cout << a++ << " " << b-- << " ";
        }
        cout << a;
    }
}

int main()
{
    int n;
    cin >> n;
	solve(n);
	return 0;
}
