#include <bits/stdc++.h>
#define ln '\n'
#define int long long
using namespace std;
const int mod = 1e9 + 7;
 
int binpow(int b,int p,int q){
    if(p == 0)
        return 1;
    int ans=binpow(b,p/2,q);
    if(p % 2 == 1)
        return (((ans * ans) % q) * b) % q;
    return (ans * ans) % q;
}
signed main(){
    int t;
    cin >> t;
    while(t--)
    {
        int a,b,c;
        cin >> a >> b >> c;
        int p=binpow(b,c,mod-1);
        cout << binpow(a,p,mod)<< ln;
    }
}
