#include<iostream>
#include<vector>
#include<queue>
#include<cstdio>
using namespace std;
#define mx 2000
int dp[mx][mx];
int main()
{
  ios::sync_with_stdio(0);
  cin.tie(NULL);
  cout.tie(NULL);
  int a, b, c = -1;
  char ch;
  cin >> a >> b;
  queue<vector<int>>q;
  for (int i = 0; i < a; i++) {
    for (int j = 0; j < b; j++) {
      cin >> ch;
      if (ch == '#') dp[i][j] = -1;
      if (ch == 'X') {
        dp[i][j] = 1;
        q.push({i, j});
      }
    }
  }
  dp[0][1] = dp[0][b-2] = 0;
  while (!q.empty())
  {
  	vector<int>t = q.front();
  	q.pop();
  	int x = t[0], y = t[1];
  	for (int i = -1; i < 2; i++) {
  		for (int j = -1; j < 2; j++) {
  			if (i*i+j*j==1&&x+i>=0&&x+i<a&&y+j>=0&&y+j<b&&dp[x+i][y+j]==0) {
  				dp[x+i][y+j] = dp[x][y] + 1;
  				q.push({x+i,y+j});
  			}
  		}
  	}
  }
  
  cout << dp[0][1]-2 << " " << dp[0][b-2]-2 << endl;
  if (dp[0][1] > dp[0][b-2]) cout << "Bolek";
  else cout << "Lolek";
  
  return 0;
}
