    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);
    cin >> s >> v >> k;
    for (int i = 0; s[i]; i++)
    {
        for(long long j=i, kk=k, h=0; s[j]&&(v[s[j]-'a']>'0'||kk--); j++)
