#include<bits/stdc++.h>
using namespace std;
#define rep(i, x, y) for(int i = (int)x; i < (int)y; ++ i)
#define _rep(i, x, y) for(int i = (int)x; i > (int)y; -- i)
typedef long long ll;
constexpr int mod = 1e9 + 7;
const int N = 1e6 + 10000;
ll inv[N], gl[N], gr[N];
ll qmi(ll x, ll y){
 ll res = 1;
 while(y){
  if(y & 1) (res *= x) %= mod;
  (x *= x) %= mod;
  y >>= 1;
 }
 return res;
}
int main(){
 ios::sync_with_stdio(false); 
 cin.tie(nullptr);
 cout.tie(nullptr);
 int n, k;
 cin >> n >> k;
 rep(i, 1, k + 3) 
 inv[i] = (i != 1 ? 1ll * (mod - mod / i) * inv[mod % i] % mod : 1);
 rep(i, 0, k + 3) 
 inv[i] = (i ? inv[i] * inv[i - 1] % mod : 1);
 gl[0] = 1;
 rep(i, 1, k + 3) gl[i] = (gl[i - 1] * (n - i)) % mod;
 gr[k + 3] = 1;
 _rep(i, k + 2, 0) gr[i] = (gr[i + 1] * (n - i)) % mod;
 ll f = 0, ans = 0;
 rep(i, 1, k + 3){
  (f += qmi(i, k)) %= mod;
  ll t = (gl[i - 1] * gr[i + 1] % mod * inv[i - 1] % mod * inv[k + 2 - i] % mod) % mod;
  if((k - i) & 1) t = (mod - t) % mod;
  (ans += f * t % mod) %= mod;
 }
 cout << ans;
 return 0;
}
