def solve(N):
  arr = {"9": "6", "6": "9", "8": "8", "1": "1", "0": "0"}

  lef = 0
  r = len(N) - 1
  
  while lef <= r:
      if N[lef] in arr and arr[N[lef]] == N[r]:
          lef += 1
          r -= 1            
      else:
          return 'NO'
  
  return 'YES'
print(solve(input()))
