#include<bits/stdc++.h>
using namespace std;

int main()
{
    int n;
    cin >> n;

    vector<int>a(n);

    for (int i = 0; i < n; i++)
    {
        cin >> a[i];
    }

    int d, m, l = 0, k = 0, sum = 0;

    cin >> d >> m;

    vector<int>b(n*n);
    vector<int>c(n*n);


    for (int i = 0; i < n; i++)
    {
        for (int j = i; j < n; j++)
        {
            sum = sum + a[j];
            b[l] = sum;
            k = k + 1;
            c[l] = k;
            l = l + 1;
        }
        k = 0;
        sum = 0;
    }

    int ans = 0;

    for (int i = 0; i < l; i++)
    {
        if (d == b[i] && m == c[i]) ans = ans + 1;
    }

    cout << ans;

    return 0;
}
