#include <iostream>
#include <queue>
#include <stack>
#include <list>
#include <cmath>
#define int long long
using namespace std;

int dfs(int n) {
    if (n <= 1)
        return 1;
    return 2 * dfs(n / 2);
}

signed main()
{
    int n;
    cin >> n;
    cout << dfs(n);

    return 0;
