#include<bits/stdc++.h>
#define int long long
using namespace std;

int dfs(int a[], int n)
{
	int l = INT_MIN, k = 0;

	for (int i = 0; i < n; i++)
	{
		k = k + a[i];
		if (l < k)
			l = k;

		if (k < 0)
			k = 0;
	}
	return l;
}

signed main()
{
    int n;
    cin >> n;
	int a[n];
	for (int i = 0; i < n; i++) {
        cin >> a[i];
	}
	cout << dfs(a, n) << endl;
	return 0;
}
