#include <bits/stdc++.h>
#define int long long 
#define mod 1000000007
#define mx 2000002
int dp[mx], arr[22];

using namespace std;

int solve(int m, int n){        
	dp[0] = 1;         
	for(int i = 0; i < m; i++){         
		for(int j = arr[i]; j <= n; j++){
			dp[j] += dp[j-arr[i]]; 
			dp[j] %= mod;
		}
	}
	return dp[n];         
}
signed main(){
  
  int p = 1;
  for (int i = 0; i < 22; i++) {
	arr[i] = p;
	p *= 2;
  }
  
  int n; 
  cin >> n;
  cout << solve(22, n) % mod << endl;
  
  return 0;
}
