#include<bits/stdc++.h>
#define int long long
using namespace std;
#define mx 600000
int dp[mx];

bool check(int x, int y, int a, int b) {
    for (int i = x; i <= y; i++) {
        if (dp[i] != dp[a+i]) {
            return false;
        }
    }
    return true;
}

signed main()
{
    ios_base::sync_with_stdio( 0 );
    cin.tie(NULL);
    cout.tie(NULL);
    cin >> dp[0] >> dp[1] >> dp[2];
    dp[0] %= 10;
    dp[1] %= 10;
    dp[2] %= 10;

    int t = -1;
    for (int i = 3; i < mx; i++) {
        dp[i] = (dp[i-1] + dp[i-2] + dp[i-3]) % 10;
        if (i > 4 && i % 2 && check(0, i/2, i/2+1, i)) {
            t = i + 1;
            if (i > 10000) {
                break;
            }
        }
    }

    //cout << t << endl;
    int q, k;
    cin >> q;
    //1 2 3
    while (q--) {
        cin >> k;
        cout << dp[k % t] << " ";
    }

	return 0;
}
