def C(n, r):
	
	if (r > n):
		return 0
		
	m = 1000000007
	D = [0 for i in range(r + 1)]
	D[0] = 1;
	if(r+1>=2):
		D[1] = 1
	for i in range(2, r + 1):
		D[i] = m - (m // i) * D[m % i] % m

	ans = 1

	for i in range(2, r + 1):
		ans = ((ans % m) * (D[i] % m)) % m
	for i in range(n, n - r, -1):
		ans = ((ans % m) * (i % m)) % m
		
	return ans
n, r = map(int,input().split())
print(C(n, r))
