#include<bits/stdc++.h>
using namespace std;

set < int > st;

bool isgcd(int x, int a, int b) {
	if (__gcd(a, b) == x) 
		return true;
	return false;
}

bool islcm(int x, int a, int b) {
	if ((a / __gcd(a, b)) * b == x) 
		return true;
	return false;
}

