	//cout << n<< " " << k << endl << endl;
	scanf("%d%d",&n,&k);
	
	for (int i = 1; i * i <= k; i++) {
		if (k % i == 0) {
			st.insert(i);
			st.insert(k/i);
		}
	}
	
	int m = st.size();
	int arr[m], i = 0;
	set < int > :: iterator it;
	for (it=st.begin();it!=st.end();it++) {
		arr[i++] = *it;
	}
	int mn = INT_MIN, a = -1, b = -1;
	for (int i = 0; i < m; i++) {
		for (int j = i; j < m; j++) {
			if (arr[j]%arr[i]!=0&&isgcd(n, arr[i], arr[j]) &&
				islcm(k, arr[i], arr[j])){
				//cout << arr[i] << " " << arr[j] << endl;
				if (abs(arr[i]-arr[j])>mn) {
				mn = abs(arr[i]-arr[j]);
					a = arr[i];
					b = arr[j];
				}
			}
		}
	}
	//cout << endl;
	if (a == -1) 
	cout << -1;
	else
	cout << a << " " << b;
	return 0;
