#include <bits/stdc++.h>
using namespace std;

#define mx 1000001
int a[mx], b[mx];

int main()
{
    
    int n, k;
    cin >> n >> k;
    
    
    int i = 2, x = k;
    vector < int > p;
    while (i * i <= k) {
        while (k % i == 0) {
            k /= i;
            a[i]++;
        }
        if (a[i]) p.push_back(i);
        i++;
    }
    
    if (k > 1) {
        p.push_back(k);
        a[k]++;
    }
    
    for (int i = 2; i <= n; i++) {
        k = i;
        int j = 0;
        while (j < p.size() && 1 <= k) {
            while (k % p[j] == 0) {
                k /= p[j];
                b[p[j]]++;
            }
            j++;
        } 
    }
    int res = INT_MAX;
    for (int i = 2; i <= x; i++) {
        if (a[i] > 0) {
            res = min(res, int(b[i]/a[i]));
        }
    }
    
    cout << res;
    
    return 0;
}
