#include<bits/stdc++.h>
using namespace std;
int solve(string s) {
	int p[] = {0, 1};
	for (int i = 0; s[i]; i++) {
		p[49 - s[i]] = min(p[0], p[1]) + 1;
	}
	return p[0];
}
signed main() {
	string s;
	cin >> s;
	cout << solve(s) << endl;
	return 0;
}
