#include<bits/stdc++.h>
using namespace std;
string bin(long long n) {
 string bn = "";
 while (n) {
  bn += char(n % 2 + 48);
  n >>= 1;
 }
 return bn;
}
int solve(string s) {
 int p[] = {0, 1};
 for (int i = 0; s[i]; i++) {
  p[49 - s[i]] = min(p[0], p[1]) + 1;
 }
 return p[0];
}
signed main() {
 long long s;
 cin >> s;
 cout << solve(bin(s)) << endl;
 return 0;
}
