#include <bits/stdc++.h>
using namespace std;

int solve() {

    string s1, s2;
    int u, v, x, y;
    cin >> s1 >> s2;

    u = s1[0] - 96;
    v = s1[1] - 48;

    x = s2[0] - 96;
    y = s2[1] - 48;

    int mx = max(abs(x - u), abs(y - v));
    return mx / 2 + mx % 2;
}

int main() {
	int test = 1;
	while (test--) cout << solve() << endl;
	return 0;
}
