#include <bits/stdc++.h>
using namespace std;
char dfs(vector<int>& dp)
{
	int m = 0;
	char ch;
	for (int i = 0; i < 26; i++) {
		if (dp[i] > m) {
			m = dp[i];
			ch = 'a' + i;
		}
	}

	return ch;
}

string solve(string S)
{
	int n = S.size();
	if (n == 0)
		return "";

	vector<int> dp(26, 0);
	for (auto& ch : S)
		dp[ch - 'a']++;

	char ch_max = dfs(dp);
	int mx = dp[ch_max - 'a'];
	if (mx > (n + 1) / 2)
		return "";

	string res(n, ' ');
	int ind = 0;
	while (mx) {
		res[ind] = ch_max;
		ind = ind + 2;
		mx--;
	}

	dp[ch_max - 'a'] = 0;
	for (int i = 0; i < 26; i++) {

		while (dp[i] > 0) {

			ind = (ind >= n) ? 1 : ind;
			res[ind] = 'a' + i;
			ind += 2;
			dp[i]--;
		}
	}

	return res;
}
int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);
	string s = "", y;
    for (int i = 0; i < 26; i++) {
        int x;
        cin >> x;
        for (int j = 0; j < x; j++) {
            s += char(i+97);
        }
    }
	y = solve(s);
	cout << (y.size() == 0 ? "-1" : y);

	return 0;
}
