#include<bits/stdc++.h>
using namespace std;

vector < int > adj[303];
int vis[303];

int BFS()
{
    int a, b;
    cin >> a >> b;
    if (a == b) {
        return 0;
    }
    for (int i = 0; i < 303; i++) {
        vis[i] = 0;
    }

    queue < int > q;
    q.push(a);
    vis[a] = 1;

    while (!q.empty())
    {
        int x = q.front();
        q.pop();
        for (auto y : adj[x]) {
            if (vis[y] == 0) {
                vis[y] += vis[x] + 1;
                q.push(y);
            }
        }
    }

    return vis[b] - 1;

}

void add(int u, int v)
{
    adj[u].push_back(v);
    adj[v].push_back(u);
}

int main()
{
    int n, m;
    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        add(u, v);
    }

    int test;
    cin >> test;

    while (test--)
    {
        cout << BFS() << endl;
    }

    return 0;
}
