def solve(arr, s):

	for i in range(6):
		for j in range(6):
			for k in range(6):
				if i != j and i != k and j != k and (arr[i] + arr[j] + arr[k]) == s - (arr[i] + arr[j] + arr[k]):
					return 'yes'
	return 'no'

arr = list(map(int,input().split()))
sm = sum(arr)

print(solve(arr, sm))
