#include <bits/stdc++.h>
using namespace std;
#define mx 300003
char a[mx], b[mx], p[mx];
signed main() {

    scanf("%s%s", a, b);
    int n = strlen(a);

    sort(a, a + n);
    sort(b, b + n);
    reverse(b, b + n);

    int x = 0, y = (n+1) / 2, r = n;
    int u = 0, v = n / 2, l = 0;

    for (int i = 0; i < n; i++){
        if(i % 2){
            if(a[x] < b[u]) p[l++] = b[u++];
            else p[--r] = b[--v];
        }
        else{
            if(a[x] < b[u]) p[l++] = a[x++];
            else p[--r] = a[--y];
        }
    }
    puts(p);
  
    return 0;
}
