#include<bits/stdc++.h>
#define int long long
using namespace std;

const int MAX = 1000;
int matrix[MAX][MAX];

int32_t main()
{
    int n;
    cin >> n;
    int ans = 0;
    int sum = 0;
    int maxx = 0;

    for (int i = 0; i < n; i++)
    {
        for (int j = 0; j < n; j++)
        {
            cin >> matrix[i][j];
            ans += matrix[i][j];
            sum += matrix[i][j];
        }
        if (maxx < sum) maxx = sum;
        sum = 0;
    }
    sum = 0;
    for (int i = 0; i < n; i++)
    {
        for (int j = 0; j < n; j++)
        {
            sum += matrix[j][i];
        }
        if (maxx < sum) maxx = sum;
        sum = 0;
    }
    cout << maxx * n - ans;
}
