#include <bits/stdc++.h>
    #define int long long
    using namespace std;

    int countPair(string str[], int n)
    {
        int cnt[1 << 10] = { 0 };

        for (int i = 0; i < n; i++) {

            int mask = 0;
            for (int j = 0; str[i][j] != '\0'; ++j)
                mask |= (1 << (str[i][j] - '0'));
            cnt[mask]++;
        }

        int ans = 0;
        for (int m1 = 0; m1 <= 1023; m1++)
            for (int m2 = 0; m2 <= 1023; m2++)
                if ((m1 | m2) == 1023) {

                    ans += ((m1 == m2) ?
                        (cnt[m1] * (cnt[m1] - 1)) :
                        (cnt[m1] * cnt[m2]));
                }

        return ans / 2;
    }

    int32_t main()
    {
        //cout << (1 << 10);
        int n;
        cin >> n;

        string str[n];
        for (int i = 0; i < n; i++){
            cin >> str[i];
        }
        cout << countPair(str, n) << endl;
        return 0;
    }
