#include<bits/stdc++.h>
using namespace std;

#define mx 10000001
int prime[mx];

void sawe()
{
    prime[0] = prime[1] = 1;
    for (int i = 2; i * i < mx; i++) {
        if (prime[i] == 0)
        {
            for (int j = i * i; j < mx; j += i) {
                prime[j] = 1;
            }
        }
    }
}

signed main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    sawe();
    int n;
    cin >> n;
    while (n--)
    {
        int x;
        cin >> x;
        int s = 0;
        int i = 2;
        while (x >= i*i) {
            if (prime[x] == 0) {
                break;
            }
            else {
            while (x % i == 0) {
                x /= i;
                s++;
            }
            i++;
            }
        }
        if (x > 1) s++;
        cout << s << endl;
    }

    return 0;
}
