def solve(a, b):

	x = bin(a)[2:]
	y = bin(b)[2:]

	if len(x) > len(y):
		y = '0' * (len(x) - len(y)) + y
	else:
		x = '0' * (len(y) - len(x)) + x

	ind = -1

	for i in range(len(x)):
		if x[i] == y[i]:
			ind = i;

	if ind == -1:
		return int('1' + '0' * len(x), 2)

	return int('1' + '0' * (len(x) - ind - 1), 2)

A = int(input())
B = int(input())
print(solve(A, B))
