#include<bits/stdc++.h>
using namespace std;
map<int,int>mp;
int tmp,i,ans;
string s;
int main(){
    mp[0]=1;
    for(cin >> s;s[i];i++){
        if(s[i]=='0')tmp++;
        else tmp--;
        if(!mp[tmp])mp[tmp]=i+2;
            ans=max(ans,i-mp[tmp]+2);
    }
    cout<<ans;
    return 0;
}
