#include<bits/stdc++.h>
using namespace std;
int main(){
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    
	int n;
	cin >> n;
	int a[n];
	for (int i = 0; i < n; i++) cin >> a[i];
	string s;
	cin >> s;
	int mx = 0;
	for (int i = 0; i < n-1; i++) {
        mx = max(mx, a[i]);
        if (s[i] == '0' && mx > i+1)
            return cout << "No\n", 0;
	}
	cout << "Yes";
	
	
	return 0;
}
