#include <bits/stdc++.h>
using namespace std;
#define ll long long
int aaa[22222];
int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0);
    ll n,q,l,r,d;
    cin>>n;
    vector<ll>a(n),b(n+2,0);
    for(int i=0;i<n;i++) cin>>a[i];
    cin>>q;
    for(int tt=0;tt<q;tt++){
        cin>>l>>r>>d;
        b[l-1]+=d;
        ll x = d*(r-l+2);
        if(r<n) b[r]-=x, b[r+1]+=x-d;
    }
    for(int i=1;i<n;i++) b[i]=b[i-1]+b[i];
    for(int i=1;i<n;i++) b[i]=b[i-1]+b[i];
    for(int i=0;i<n;i++) cout<<a[i]+b[i]<<" ";
}
