#include <iostream>
#define int long long
using namespace std;

bool isprime(int n)
{
    for (int i = 2; i * i <= n; i++)
    {
        if (n % i == 0) return false;
    }
    return true;
}

int32_t main()
{

    int n;
    cin >> n;
    
    if (n == 1 || n == 2) return cout << n, 0;
    if (n == 3) return cout << 2, 0;
    if (n == 4) return cout << 4, 0;
    if (isprime(n + 1)) return cout << n, 0;
    cout << 0;
    return 0;
}
