#include<bits/stdc++.h>
#define int long long
using namespace std;
signed main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);

    int n;
    cin >> n;
    int a[n+1]={0ll}, b[n+1]={0ll};
    for (int i = 1; i <= n; i++) {
        cin >> b[i];
        a[i] = b[i] - b[i-1];
    }

    int test;
    cin >> test;

    while (test--)
    {
        int l, r, x;
        cin >> l >> r >> x;
        a[l] += x;
        if (r < n) {
            a[r+1] -= x;
        }
    }

    for (int i = 1; i <= n; i++) {
        a[i] += a[i-1];
        cout << a[i] << ' ';
    }


    return 0;
}
