x, y = input().split('=')
a, b, c = [], [], ''
for i in x + '+':
    if i in '+-*/':
        b.append(i.replace('/', '//'))
        a.append(str(int(c, 2)))
        c = ''
    else:
        c += i
c = a[0]
for i in range(1, len(b)):
    c += b[i - 1] + a[i]
